package org.example;

import edu.stanford.nlp.pipeline.*;
import edu.stanford.nlp.sentiment.SentimentCoreAnnotations;
import edu.stanford.nlp.ling.CoreAnnotations;
import edu.stanford.nlp.util.CoreMap;
import org.apache.commons.csv.CSVFormat;
import org.apache.commons.csv.CSVParser;
import org.apache.commons.csv.CSVRecord;

import java.io.FileReader;
import java.io.IOException;
import java.io.Reader;
import java.util.Properties;

public class App {
    
    private StanfordCoreNLP pipeline;
    
    public App() {
        Properties props = new Properties();
        props.setProperty("annotators", "tokenize,ssplit,parse,sentiment"); // разбиение текста на слова, предложения, построение синтаксического дерева, оценка предложения
        props.setProperty("parse.model", "edu/stanford/nlp/models/lexparser/englishPCFG.ser.gz"); //путь к обученной модели для парсинга текста на англ
        props.setProperty("sentiment.model", "edu/stanford/nlp/models/sentiment/sentiment.ser.gz"); //путь к модели для анализа тонельности текста
        this.pipeline = new StanfordCoreNLP(props);
    }
    
    public String analyzeSentiment(String text) {
        if (text == null || text.trim().isEmpty()) {
            return "neutral";
        }
        
        // Очистка текста от тэгов
        String cleanText = text.replaceAll("<br />", " ").replaceAll("<[^>]+>", " ").replaceAll("\\s+", " ").trim();
        
        
        Annotation document = new Annotation(cleanText);
        pipeline.annotate(document);
        
        int positiveCount = 0;
        int negativeCount = 0;
        int neutralCount = 0;
        
        for (CoreMap sentence : document.get(CoreAnnotations.SentencesAnnotation.class)) {
            String sentiment = sentence.get(SentimentCoreAnnotations.SentimentClass.class);
            
            switch (sentiment) {
                case "Very positive":
                case "Positive":
                    positiveCount++;
                    break;
                case "Very negative":
                case "Negative":
                    negativeCount++;
                    break;
                default:
                    neutralCount++;
                    break;
            }
        }
        
        if (positiveCount > negativeCount && positiveCount > neutralCount) {
            return "positive";
        } else if (negativeCount > positiveCount && negativeCount > neutralCount) {
            return "negative";
        } else {
            return "neutral";
        }
    }
    
    /**
     * Processes CSV file with reviews
     * @param csvFilePath path to CSV file
     * @param limit maximum number of reviews to process (0 = all)
     */
    public void processReviews(String csvFilePath, int limit) {
        try (Reader reader = new FileReader(csvFilePath);
             CSVParser csvParser = new CSVParser(reader, CSVFormat.DEFAULT.withFirstRecordAsHeader().withIgnoreHeaderCase().withTrim())) {
            int count = 0;
            int correct = 0;
            int total = 0;
            
            for (CSVRecord record : csvParser) {
                if (limit > 0 && count >= limit) {
                    break;
                }
                
                String review = record.get("review");
                String actualSentiment = record.get("sentiment");
                String predictedSentiment = analyzeSentiment(review);
                System.out.println("Review " + (count + 1));
                
                // Вывод первых 100 символов отзыва
                String shortReview = review.length() > 100 ? 
                    review.substring(0, 100) + "..." : review;
                System.out.println("First 200 characters of review: " + shortReview.replaceAll("<br />", " ").replaceAll("<[^>]+>", ""));
                
                System.out.println("Actual sentiment: " + actualSentiment);
                System.out.println("Predicted sentiment: " + predictedSentiment);
                
                if (predictedSentiment.equals(actualSentiment)) {
                    correct++;
                }
                
                total++;
                count++;
            }
            
            System.out.println("Accuracy: " + String.format("%.2f%%", (correct * 100.0 / total)));
            
        } catch (IOException e) {
            System.err.println("Error reading file: " + e.getMessage());
            e.printStackTrace();
        }
    }
    
    public String getGreeting() {
        return "Movie Review Sentiment Analyzer";
    }

    public static void main(String[] args) {
        System.out.println(new App().getGreeting());
        
        App app = new App();
        
        String csvPath = "C:\\Users\\olegl\\all\\polytech\\параллельное программирование\\j25-26\\tasks\\term-1\\5\\IMDB Dataset.csv";
        
        app.processReviews(csvPath, 10);
    }
}
